mapper.select:
SELECT * FROM ( SELECT A.BOARD_CONTENT_NO AS boardContentNo, A.ORG_BOARD_CONTENT_NO AS orgBoardContentNo, A.TITLE AS title, A.CONTENT AS content, A.SEC_YN AS secYn, A.USER_NO AS userNo, A.VIEW_CNT AS viewCnt, IF((DATE_FORMAT(A.REG_DATE,'%Y%m%d')=DATE_FORMAT(now(),'%Y%m%d')),DATE_FORMAT(A.REG_DATE,'%H:%i:%S'),DATE_FORMAT(A.REG_DATE,'%Y.%m.%d') ) AS regDate, A.BOARD_NO AS boardNo, B.USER_NAME AS userName, C.BOARD_TYPE_CODE AS boardTypeCode, (SELECT count(BOARD_CONTENT_NO) FROM AI_BOARD_CONTENT ABC WHERE ABC.ORG_BOARD_CONTENT_NO = A.BOARD_CONTENT_NO) AS replyCnt, A.CONTENT_GROUP AS contentGroup FROM AI_BOARD_CONTENT A INNER JOIN AI_USER B ON A.USER_NO = B.USER_NO INNER JOIN AI_BOARD C ON A.BOARD_NO = C.BOARD_NO WHERE A.ORG_BOARD_CONTENT_NO is NULL
AND A.BOARD_NO = #{boardNo}
AND C.BOARD_TYPE_CODE = #{boardTypeCode}
AND C.SPORTS_TYPE_CODE = #{sportsTypeCode}
AND A.USER_NO = #{userNo}
AND (A.TITLE like CONCAT('%',#{searchText},'%') or A.CONTENT like CONCAT('%',#{searchText},'%'))
) A WHERE 1=1
AND A.replyCnt > 0
AND A.replyCnt = 0
ORDER BY A.contentGroup DESC, A.regDate ASC LIMIT #{pagePerRow} offset #{startRow}

SELECT COUNT(*) FROM ( SELECT (SELECT count(BOARD_CONTENT_NO) FROM AI_BOARD_CONTENT ABC WHERE ABC.ORG_BOARD_CONTENT_NO = A.BOARD_CONTENT_NO) AS replyCnt FROM AI_BOARD_CONTENT A INNER JOIN AI_USER B ON A.USER_NO = B.USER_NO INNER JOIN AI_BOARD C ON A.BOARD_NO = C.BOARD_NO WHERE A.ORG_BOARD_CONTENT_NO is NULL
AND A.BOARD_NO = #{boardNo}
AND C.BOARD_TYPE_CODE = #{boardTypeCode}
AND C.SPORTS_TYPE_CODE = #{sportsTypeCode}
AND (A.TITLE like CONCAT('%',#{searchText},'%') or A.CONTENT like CONCAT('%',#{searchText},'%'))
AND A.USER_NO = #{userNo}
) A WHERE 1=1
AND A.replyCnt > 0
AND A.replyCnt = 0

SELECT BOARD_NO as boardNo, NAME as name, BOARD_TYPE_CODE as boardTypeCode, REG_DATE as regDate, REG_USER_NO as regUserNo, UPD_DATE as updDate, UPD_USER_NO as updUserNo FROM AI_BOARD WHERE 1=1
AND BOARD_NO = #{boardNo}
AND BOARD_TYPE_CODE = #{boardTypeCode}
AND SPORTS_TYPE_CODE = #{sportsTypeCode}

mapper.insert:
SELECT ai_get_seq('boardContentNo') FROM DUAL
INSERT INTO AI_BOARD_CONTENT ( BOARD_CONTENT_NO, USER_NO, BOARD_NO, ORG_BOARD_CONTENT_NO, TITLE, CONTENT, SEC_YN, VIEW_CNT, REG_DATE, UPD_DATE, CONTENT_GROUP ) VALUES ( #{boardContentNo}, #{userNo}, #{boardNo}, #{orgBoardContentNo}, #{title}, #{content}, #{secYn}, 0, now(), now(),
#{orgBoardContentNo}
#{boardContentNo}
)
